class MarvelService {

  _apiBase = 'https://gateway.marvel.com:443/v1/public/';
  _apiKey = 'apikey=a6128fd425a3d423c5419288edd4c5a7';



  getResource = async (url) => {
    let res = await fetch(url);

    if(!res.ok){
      throw new Error (`Could not fetch ${url}, status: ${res.status}`);
    }

    return await res.json();
  }

  getAllCharacters = () => {
    return this.getResource(`characters?limit=9&offset=210&apikey=a6128fd425a3d423c5419288edd4c5a7`);
  }
  getCharacter = (id) => {
    return this.getResource(`https://gateway.marvel.com:443/v1/public/characters/${id}?&apikey=a6128fd425a3d423c5419288edd4c5a7`);
  }

}


export default MarvelService;
