import { Component } from 'react';
import MarvelService from '../../services/MarvelService';

import './charList.scss';
import abyss from '../../resources/img/abyss.jpg';
import content from '../randomChar/RandomChar'

class CharList extends Component {
    state = {
        char: {},
        loading: true,
        error: false
    }

    componentDidMount() {
        this.updateChar();
    }

    onCharLoaded = (char) => {
        this.setState({
            char, 
            loading: false
        })
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true
        })
    }
    onCharLoading = () => {
        this.setState({
            loading: true
        })
    }

    marvelService = new MarvelService();

    updateChar = () => {
        this.onCharLoading();
        this.marvelService
            .getAllCharacters()
            .then(this.onCharLoaded)
            .catch(this.onError);
    }






    render() {
        const {char, loading, error} = this.state;

